import React, {useEffect} from "react";
import { Button, Form } from "react-bootstrap";
import axios from "axios"


const Profile = props => {


  useEffect(() => {
    const id = this.state.user._id

    axios
      .get(`/profile/${id}`)
      .then(response => {
        console.log(response.data);
        this.setState({
          profile: response.data,
          username: response.data.username,
          urlPath: response.data.urlPath,
          age: response.data.age,
          gender: response.data.gender,
          languages: response.data.languages,
          about: response.data.about
        });
      })
      .catch(err => {
        if (err.response.status === 404) {
          this.setState({
            error: err.response.data.message
          });
        }
      })
  }, []);

  return(

    // if (this.state.error) {
    //   return <p>{this.state.error}</p>;
    // } else if (this.state.profile === null) {
    //   return <div></div>;
    // }

      <div>
        <h2>Hey {props.username}</h2>
      <img src={props.urlPath} alt="profile"/>
        <p>Age: {props.age}</p>
        <p>Gender: {props.gender}</p>
        <p>Languages: {props.language}</p>
        <p>About: {props.about}</p>


        <Button onClick={props.toggleEditProfile}>Show Edit Form</Button>

        {props.editForm && (
          <Form onSubmit={props.handleSubmitProfile} encType="multipart/form-data">

          <Form.Group>
              <Form.Label htmlFor="urlPath">Upload Profile Picture</Form.Label>
              <Form.Control
                type="file"
                name="urlPath"
                id="urlPath"
                onChange={props.imageUpload}
              />

            </Form.Group>
            <Form.Group>
              <Form.Label htmlFor="username">username: </Form.Label>
              <Form.Control
                type="text"
                name="username"
                id="username"
                value={props.username}
                onChange={props.handleChangeProfile}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label htmlFor="age">age: </Form.Label>
              <Form.Control
                type="number"
                name="age"
                id="age"
                value={props.age}
                onChange={props.handleChangeProfile}
              />
            </Form.Group>

            <label htmlFor="gender">Gender: </label>
            <select onChange={props.handleChangeProfile} name="gender" id="gender">
              <option value="diverse">Divers</option>
              <option value="female">Female</option>
              <option value="male">Male</option>
            </select>

            <Form.Group>
              <Form.Label htmlFor="languages">languages: </Form.Label>
              <Form.Control
                type="text"
                name="languages"
                id="languages"
                value={props.languages}
                onChange={props.handleChangeProfile}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label htmlFor="about">about: </Form.Label>
              <Form.Control
                type="text"
                name="about"
                id="about"
                value={props.about}
                onChange={props.handleChangeProfile}
              />
            </Form.Group>


            <Button type="submit">Save Profile</Button>
          </Form>

      )}
      </div>
    )

  }

export default Profile;
