  const handleLogout = () => {
    logout();

    props.clearUser(null);
  };

  return (
    <Nav className="nav justify-content-end" bg="light">
      <Link to="/">Home</Link>
      {props.user ? (
        <>
          <Link to="/" onClick={handleLogout}>
