import React from 'react';
import './App.css';
import Navbar from "./components/Navbar";
import { Route } from "react-router-dom";
import Signup from "./components/Signup"
import Login from "./components/Login"
// import { Link, Switch } from "react-router-dom";
import Posts from "./components/Post/Posts";
import NewPost from "./components/Post/PostForm";

import Profile from "./components/Profile";
import Home from "./components/Home";
import PostDetail from "./components/Post/PostDetail"


class App extends React.Component {
  state = {
    user: this.props.user,
    posts: [],
    profile: {
      error: "",
      profile: "",
      editForm: false,
      username: "",
      urlPath: "https://res.cloudinary.com/mariakristiina/image/upload/v1575542831/avatar-orange_q8rkfz.png",
      age: 0,
      gender: "",
      languages: "",
      about: String
    },
  };
//-----------------------User func------------
  setUser = user => {
    this.setState({
      user: user
    });
  };

  //-----------------------Profile func------------

  handleChangeProfile = event => {
    const {name,value} =event.target
    this.setState({
   profile:{[name]:value}
    });
  };

  toggleEditProfile = () => {
    this.setState({
      editForm: !this.state.editForm
    });
  };

  handleSubmitProfile = event => {
    event.preventDefault();

    const id = this.user.id;

    axios
      .put(`/profile/${id}`, {
        username: this.state.username,
        age: this.state.age,
        gender: this.state.gender,
        languages: this.state.languages,
        about: this.state.about,
        urlPath:this.state.urlPath
      })
      .then(response => {
        this.setState({
          profile: response.data,
          editForm: false
        });
      })
      .catch(err => {
        console.log(err);
      });
  };




  render() {
    return (
      <div className="App">
        <Navbar user={this.state.user} clearUser={this.setUser} />

        <Home />

        <Route
          exact
          path="/login"
          render={props => <Login {...props} setUser={this.setUser} />}
        />

        <Route exact path="/profile/:id" render={props => <Profile user={this.state.user} profile={this.state.profile} handleChangeProfile={this.handleChangeProfile} toggleEditProfile={this.toggleEditProfile} {...props} />} />

        <Route
          exact
          path="/signup"

          render={props => <Signup {...props} setUser={this.setUser} />}
        />

        <Route exact path="/posts" render={props => <Posts {...props} serUser={this.setUser} />} />

        <Route exact path="/post/:id" render={props => <PostDetail {...props} post={this.state.posts} />} />

        <Route exact path="/post/new" render={props => <NewPost {...props} setUser={this.setUser} handleChange={this.handleChange} />} />

      </div>
    )

  }


}


export default App
