import React from "react";
import "./App.css";
import Navbar from "./components/Navbar";
import { Route, Switch } from "react-router-dom";
import Signup from "./components/Signup";
import Login from "./components/Login";
// import { Link, Switch } from "react-router-dom";
import Posts from "./components/Post/Posts";
import About from "./components/About"

import Profile from "./components/Profile";
import Home from "./components/Home";
import PostDetail from "./components/Post/PostDetail";
import axios from "axios";
import MailboxWrapper from "./components/Post/MailboxWrapper";
import MailboxDetail from "./components/Post/MailboxDetail";



class App extends React.Component {
  state = {
    user: this.props.user,
    profile: {
      error: "",
      username: "",
      urlPath:
        "https://res.cloudinary.com/mariakristiina/image/upload/v1575542831/avatar-orange_q8rkfz.png",
      age: "",
      gender: "",
      languages: [],
      about: ""
    },
    editProfileForm: false,
    postDetail: {
      title: "",
      date: "",
      startTime: "",
      endTime: "",
      postType: "",
      category: "",
      description: "",
      owner: "",
      match: "",
      messages: ""
    },
    currentLanguage: "English",
    hover: false
  };

  // =================== user functions
  setUser = user => {
    this.setState({
      user: user
    });
  };



  //-----------------------Profile func------------

  getDataProfile = () => {
    const id = this.state.user._id;

    axios
      .get(`/profile/${id}`)
      .then(response => {
        console.log(response.data);
        this.setState(
          {
            profile: {
              username: response.data.username,
              age: response.data.age,
              gender: response.data.gender,
              languages: response.data.languages,
              about: response.data.about,
              urlPath: response.data.urlPath,
              language: response.data.language,
              siteLanguage: response.data.siteLanguage
            }
          },
          () => console.log("state after data call", this.state)
        );
      })
      .catch(err => {
        if (err.response.status === 404) {
          this.setState({
            error: err.response.data.message
          });
        }
      });
  };

  handleChangeProfile = event => {
    const { name, value } = event.target;
    this.setState({
      profile: { ...this.state.profile, [name]: value }
    });
  };

  toggleEditProfile = () => {
    console.log(this.state.editProfileForm);
    this.setState({
      editProfileForm: !this.state.editProfileForm
    });
  };

  handleSubmitProfile = event => {
    event.preventDefault();

    const id = this.state.user._id;
    const {
      username,
      age,
      gender,
      languages,
      about,
      urlPath
    } = this.state.profile;
    axios
      .put(`/profile/${id}`, {
        username,
        age,
        gender,
        languages,
        about,
        urlPath
      })
      .then(response => {
        this.setState({
          profile: response.data,
          editProfileForm: false
        });
      })
      .catch(err => {
        console.log(err);
      });
  };

  imageUpload = event => {
    console.log(event.target.files[0]);
    const file = event.target.files[0];
    let upload = new FormData();
    console.log(upload);
    const id = this.state.user._id;

    upload.append("urlPath", file);
    axios.post(`/profile/${id}`, upload).then(response => {
      console.log(response.data.secure_url);
      this.setState({
        profile: {
          ...this.state.profile,
          urlPath: response.data.secure_url
        }
      });
      console.log(this.state.profile.urlPath);
    });
  };

  // POST detail functions

  // languages functions

  handleChangeLanguages = event => {
    if(!this.state.user) {
    this.setState(
      {
        currentLanguage: event.target.value
      },
      () => console.log(this.state.currentLanguage)
    );}
    else {
      this.setState(
        {
          currentLanguage: event.target.value
        })
      //console.log(this.state.currentLanguage);
      axios
        .put("/profile/language", {
          siteLanguage: event.target.value
        })
        .then(response => {
          console.log(response.data);
          console.log(this.state.currentLanguage);
          })
        .catch(err => {
          return err;
        });
    }
  };

  changeLanguageLogin = () => {
    this.setState({
      currentLanguage: this.state.user.siteLanguage
    })
  }

  // HOVERING

  mouseOver = () => {
    this.setState({
      hover: true
    })
  }

  mouseOut = () => {
    this.setState({
      hover: false
    })
  }

  render() {
    console.log("user from app: ", this.state.user);
    //   {/* <Route
    //     exact
    //     path="/messages/detail/:id"
    //     render={props => (
    //       <Mailbox {...props}>
    //         {" "}
    //         <MailboxDetail {...props} user={this.state.user} />{" "}
    //       </Mailbox>
    //     )}
    //   />
    // </div> */}
    return (
      <div className="App">
        <Navbar
          user={this.state.user}
          clearUser={this.setUser}
          handleChangeLanguages={this.handleChangeLanguages}
          currentLanguage={this.state.currentLanguage}
        />

        <Route
          exact
          path="/"
          render={props => (
            <Home
              user={this.state.user}
              currentLanguage={this.state.currentLanguage}
              mouseOver={this.m}
              {...props}
            />
          )}
        />

        <Route
          exact
          path="/about"
          render={props => (
            <About
              user={this.state.user}
              currentLanguage={this.state.currentLanguage}
              {...props}
            />
          )}
        />

        <Route
          exact
          path="/login"
          render={props => <Login 
          {...props} 
          setUser={this.setUser} 
          currentLanguage={this.state.currentLanguage} 
          user={this.state.user}
          changeLanguageLogin={this.changeLanguageLogin}
          />}
        />
        <Route
          exact
          path="/profile/:id"
          render={props => (
            <Profile
              user={this.state.user}
              profile={this.state.profile}
              handleChangeProfile={this.handleChangeProfile}
              toggleEditProfile={this.toggleEditProfile}
              handleSubmitProfile={this.handleSubmitProfile}
              getDataProfile={this.getDataProfile}
              imageUpload={this.imageUpload}
              editProfileForm={this.state.editProfileForm}
              currentLanguage={this.state.currentLanguage}
              {...props}
            />
          )}
        />
        <Route
          exact
          path="/signup"
          render={props => (
            <Signup
              {...props}
              setUser={this.setUser}
              currentLanguage={this.state.currentLanguage}
              profile={this.state.profile}
            />
          )}
        />
        <Route
          exact
          path="/posts"
          render={props => (
            <Posts {...props} setUser={this.setUser} user={this.state.user} />
          )}
        />
        <Route
          exact
          path="/post/:id"
          render={props => (
            <PostDetail
              {...props}
              postDetail={this.state.posts}
              user={this.state.user}
            />
          )}
        />
        <Route
          path="/mailbox/:user/:messageId?"
          render={props => <MailboxWrapper user={this.state.user} {...props} />}
        />


      </div>
    );
  }
}

export default App;
