import React, { Component, Link } from "react";
import axios from "axios";

class Posts extends Component {
    state = {
        posts: []
    }

    getData = () => {
        axios
        .get("/post")
        .then(response => {
            console.log(response);
            this.setState({
                post: response.data
            });
        })
        .catch(err => {
            console.log(err);
        })
    }

componentDidMount() {
    this.getData()
}

render() {
    return (
        <div>
        {this.props.posts.map(post => {
            return (
                <div key={post._id}>
                <h2>
                <Link to={`/post/${post._id}`}></Link>{post.title}
                </h2>

                </div>
            )
        })}

        </div>
    )
}

}

export default Posts;
