import React, { Component } from "react";
import axios from "axios";
import { Button, Form } from "react-bootstrap";

class Profile extends Component {
  state = {
    error: "",
    profile: "",
    editForm: false,
    username: "",
    urlPath: "https://res.cloudinary.com/mariakristiina/image/upload/v1575542831/avatar-orange_q8rkfz.png",
    age: 0,
    gender: "",
    languages: "",
    about: String
  }

  componentDidMount() {
    props.getData();
  }


  imageUpload = event => {
    console.log(event.target.files[0]);
    const file = event.target.files[0];
    let upload = new FormData();
    console.log(upload)
    const id = this.props.match.params.id;

    upload.append("urlPath", file);
    console.log(upload)
    axios.post(`/profile/${id}`, upload)
    .then(response => {
      console.log(response)
      this.setState({
        urlPath: response.data.secure_url
      });
    });
  }

  render() {
    console.log("State", this.state)
    console.log("Props", this.props)

    if (this.state.error) {
      return <p>{this.state.error}</p>;
    } else if (this.state.profile === null) {
      return <div></div>;
    }

    return (
      <div>
        <h2>Hey {props.username}</h2>
      <img src={props.urlPath} alt="profile"/>
        <p>Age: {props.age}</p>
        <p>Gender: {props.gender}</p>
        <p>Languages: {props.language}</p>
        <p>About: {props.about}</p>


        <Button onClick={props.toggleEditProfile}>Show Edit Form</Button>

        {props.editForm && (
          <Form onSubmit={props.handleSubmitProfile} encType="multipart/form-data">

          <Form.Group>
              <Form.Label htmlFor="urlPath">Upload Profile Picture</Form.Label>
              <Form.Control
                type="file"
                name="urlPath"
                id="urlPath"
                onChange={this.imageUpload}
              />

            </Form.Group>
            <Form.Group>
              <Form.Label htmlFor="username">username: </Form.Label>
              <Form.Control
                type="text"
                name="username"
                id="username"
                value={props.username}
                onChange={props.handleChangeProfile}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label htmlFor="age">age: </Form.Label>
              <Form.Control
                type="number"
                name="age"
                id="age"
                value={props.age}
                onChange={props.handleChangeProfile}
              />
            </Form.Group>

            <label htmlFor="gender">Gender: </label>
            <select onChange={props.handleChangeProfile} name="gender" id="gender">
              <option value="diverse">Divers</option>
              <option value="female">Female</option>
              <option value="male">Male</option>
            </select>

            <Form.Group>
              <Form.Label htmlFor="languages">languages: </Form.Label>
              <Form.Control
                type="text"
                name="languages"
                id="languages"
                value={props.languages}
                onChange={props.handleChangeProfile}
              />
            </Form.Group>

            <Form.Group>
              <Form.Label htmlFor="about">about: </Form.Label>
              <Form.Control
                type="text"
                name="about"
                id="about"
                value={props.about}
                onChange={props.handleChangeProfile}
              />
            </Form.Group>


            <Button type="submit">Save Profile</Button>
          </Form>

        )}
      </div>
    )

  }

}


export default Profile;
